package main

import (
	"fmt"
	"html"
	"net/http"
	_ "net/http/pprof"
)

func main() {

	simple_handler()

}

func simple_handler() {
	http.HandleFunc("/simple", func(w http.ResponseWriter, r *http.Request) {
		fmt.Fprintf(w, "hello %q", html.EscapeString(r.URL.Path))
	})
	http.HandleFunc("/log1", func(w http.ResponseWriter, r *http.Request) {
		fmt.Println("0")
		fmt.Fprintf(w, "hello %q", html.EscapeString(r.URL.Path))
	})
	http.HandleFunc("/log10", func(w http.ResponseWriter, r *http.Request) {
		fmt.Println("0123456789")
		fmt.Fprintf(w, "hello %q", html.EscapeString(r.URL.Path))
	})
	http.HandleFunc("/log100", func(w http.ResponseWriter, r *http.Request) {
		fmt.Println("01234567890123456789012345678901234567890123456789012345678901234567890123456789012345678901234567890123456789")
		fmt.Fprintf(w, "hello %q", html.EscapeString(r.URL.Path))
	})
	http.HandleFunc("/log1000", func(w http.ResponseWriter, r *http.Request) {
		fmt.Println("")
		fmt.Fprintf(w, "hello %q", html.EscapeString(r.URL.Path))
	})
	http.HandleFunc("/log10000", func(w http.ResponseWriter, r *http.Request) {
		fmt.Println("")
		fmt.Fprintf(w, "hello %q", html.EscapeString(r.URL.Path))
	})
	http.ListenAndServe(":8080", nil)
}
